import React, { useState } from 'react';

export default function ChatBox() {
  const [isVisible, setIsVisible] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState('');

  const handleChatIconClick = () => {
    setIsVisible(true);
  }

  const handleChatCloseClick = () => {
    setIsVisible(false);
  }

  const handleUserInputChange = (event) => {
    setUserInput(event.target.value);
  }

  const handleSendMessage = () => {
    setMessages([...messages, { sender: 'You', message: userInput }]);
    setUserInput('');
  }

  const handleKeyDown = (event) => {
    if (event.keyCode === 13) {
      handleSendMessage();
    }
  }

  return (
    <div id="chat-bot">
      <div id="chat-icon" onClick={toggleChat}>
        <img src="img/chat_employee.png" alt="Chat Icon" />
      </div>
      {chatOpen && (
        <div id="chat-window">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender === 'Bot' ? 'received' : 'sent'}`}>
              <p>{message.message}</p>
            </div>
          ))}
          <div id="chat-input-container">
            <input type="text" id="chat-input" placeholder="Ask a question..." value={userInput} onChange={handleUserInput} onKeyDown={handleKeyDown} />
            <button id="chat-send-button" onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}
