import React, { useState } from 'react';

export default function ChatBox() {
  const [isVisible, setIsVisible] = useState(false);

  const handleChatIconClick = () => {
    setIsVisible(true);
  }

  const handleChatCloseClick = () => {
    setIsVisible(false);
  }

  return (
    <div id="chat-bot">
      <div id="chat-icon" onClick={handleChatIconClick}>
        <img src="img/chat_employee.png" alt="Chat Icon"/>
      </div>
      {isVisible && (
        <div id="chat-window">
          <div className="message received">
            <p>Hello! How may I help you?</p>
          </div>
          <div id="chat-messages"></div>
          <div id="chat-input-container">
            <input type="text" id="chat-input" placeholder="Ask a question..."/>
            <button id="chat-send-button">Send</button>
          </div>
          <button id="chat-close-button" onClick={handleChatCloseClick}>Close</button>
        </div>
      )}
    </div>
  );
}
