import React, { useState } from 'react';
import { createStore, connect } from 'redux';
import Axios from 'axios';

import './SignIn.css'

import Header from '../components/Header'
import Footer from '../components/Footer'
import ChatBox from '../components/ChatBox'

import MainLayout from '../layout/MainLayout';

function SignIn() {
    const [useremail, getEmail]=useState("");
    const [userpassword, getPassword]=useState("");
    const [signinMessage,getsigninMessage]=useState("");
   // let isLogged=false;
   // const [isLoggedIn, setIsLoggedIn] = useState(false); //is set true when user is logged in

   //Stores the variable is Logged in for gloabal use
    function myReducer(state = { isLoggedIn: false }, action) {
      switch (action.type) {
        case 'SET_LOGIN_STATUS':
          return { ...state, isLoggedIn: action.payload };
        default:
          return state;
      }
    }
    const store=createStore(myReducer);

    //Login user If user is
    const login = ({isLoggedIn}) => {
      Axios.post("http://localhost:3001/sign-in", {
        email: useremail,
        password: userpassword,
      }).then((response) => {
        if(response.data.message){
          console.log(isLoggedIn);
        getsigninMessage(response.data.message); //Login unsucceful
        }else{
        getsigninMessage(response.data[0].FirstName); //Login succfull
        store.dispatch({ type: 'SET_LOGIN_STATUS', payload: true });
          console.log(isLoggedIn);
        }
      });
    };
    /*
    //Logout Button
    const logout = () => {
      isLogged=false; //Set login statues to false
    };

    if(isLogged){
      return(
        <MainLayout>
        <div className="signin-container">
            <h1>Welcome {signinMessage}</h1>
            <button type="button"  onClick={logout} >Sign Out</button>
          </div>
    </MainLayout>
      );
    }
    */

  return (
    <MainLayout>
        <div className="signin-container">
            <h1>Sign In to O-CompS</h1>
            <form className="signin-form">
              <input type="email" name="email" placeholder="Email" required onChange={(e) => {getEmail(e.target.value);}}/>
              <input type="password" name="password" placeholder="Password" required onChange={(e) => {getPassword(e.target.value);}}/>
              <button type="button"  onClick={login} >Sign In</button>
            </form>
            <p><a href="#">Forgot your password?</a></p>
            <p>{signinMessage}</p>
          </div>

    </MainLayout>
  );

}

export default SignIn;
