import React, { useState } from 'react';
import MainLayout from '../layout/MainLayout'
import './UserPage.css'
import { useNavigate } from 'react-router-dom';


function UserPage(){
    const [name,Username]=useState(localStorage.getItem("FullName"));
    const [phone,Phonenumber]=useState(localStorage.getItem("Phone#"))
    const [email,getEmail]=useState(localStorage.getItem("email"))
    const [accountinfo,display]=useState();
    const navigate = useNavigate();


    const AccountInfo=()=>{
        display(
    <form className="userpage-form">
    <label>Name:</label>
    <input type="text" value={name} readonly />
    <br />
    <label>Email:</label>
    <input type="email" value={email} readonly />
    <br />
    <label>Phone:</label>
    <input type="tel" value={phone} readonly />
    </form>
        );
    };
    const logout=()=>{
        window.loginStatus=false;
        localStorage.clear();
        navigate('/sign-in');     
    }


    return(
        <MainLayout>
  <div className="userpage-container">
     <h1>Personal Page</h1>

     <div className='userpage-navbar'>
     <ul>
      <li><a href="#home" onClick={AccountInfo}>Account Information</a></li>
      <li><a href="#news">Wallet</a></li>
      <li><a href="#contact">Purchase History</a></li>
      <li><a href="#about" onClick={logout}>Logout</a></li>
     </ul>
     </div>
     <div>
  <meta charSet="utf-8" />
  <h2>Account balanace</h2>
  <form name="accountInfo">
  <input type="text" name="currentbalance" placeholder="First Name" value="$12.00" readonly/><br />
    <input type="text" name="Make a Deposit" /><br />
    <input type="button"  defaultValue="Deposit" onclick="theBankAccount.deposit();" />
    {/* <input type="button" defaultValue="Withdraw $5" onclick="theBankAccount.withdraw();" /> */}
  </form>
</div>

      </div>


            </MainLayout>


    )
}
export default UserPage;
