    <div className="element-center">
      <div id="acctBalance">
        <span className="wrap" />
        <h1>
          Total Balance
        </h1>
        <label>{totalBalanceFormatted} </label>
      </div>
      <div id="inputs">
        <h2>
          Deposit
        </h2>
        <input type="number" id="userDeposit" step="0.01" required onChange={(e) => {getoDeposit(e.target.value);}} />
        <button id="btnDeposit" onClick={Deposit}>Deposit</button>
        <h2>
          Withdraw
        </h2>
        <input type="text" id="userWithdraw" required />
        <button id="btnWithdraw">Withdraw</button>
      </div>
    </div>
  </div>
</div>
