import React, { createContext, useContext, useState } from 'react';
import testData from '../browse/testData';
import './Filter_cat.css'

const Filter_cat = ({ cat, selectedCategories, setSelectedCategories }) => {

    const handleCategoryChange = (event) => {
        if (event.target.checked) {
          setSelectedCategories([...selectedCategories, event.target.value]);
        } else {
          setSelectedCategories(
            selectedCategories.filter((category) => category !== event.target.value)
          );
        }
      };
    
    return (
        <div className="filter-cat">
            <label>
                <input type="checkbox" name="cat" value={props.cat} 
                onChange={handleCategoryChange} checked={selectedCategories.includes(cat)} />
                {props.cat}
            </label>
        </div>
    )
}

export default Filter_cat
