import React, { useState } from 'react';

function ChatBot() {
  const [chatOpen, setChatOpen] = useState(false);
  const [messages, setMessages] = useState([{ sender: 'Bot', message: 'Hello! How may I help you?' }]);
  const [userInput, setUserInput] = useState('');

  function toggleChat() {
    setChatOpen(prevOpen => !prevOpen);
  }

  function handleUserInput(event) {
    setUserInput(event.target.value);
  }

  function sendMessage() {
    if (userInput.trim() === '') {
      return;
    }

    setMessages(prevMessages => [
      ...prevMessages,
      { sender: 'You', message: userInput },
    ]);

    setUserInput('');
  }

  function handleKeyDown(event) {
    if (event.key === 'Enter') {
      sendMessage();
    }
  }

  return (
    <div id="chat-bot">
      <div id="chat-icon" onClick={toggleChat}>
        <img src="img/chat_employee.png" alt="Chat Icon" />
      </div>
      {chatOpen && (
        <div id="chat-window">
          {messages.map((message, index) => (
            <div key={index} className={`message ${message.sender === 'Bot' ? 'received' : 'sent'}`}>
              <p>{message.message}</p>
            </div>
          ))}
          <div id="chat-input-container">
            <input type="text" id="chat-input" placeholder="Ask a question..." value={userInput} onChange={handleUserInput} onKeyDown={handleKeyDown} />
            <button id="chat-send-button" onClick={sendMessage}>Send</button>
          </div>
        </div>
      )}
    </div>
  );
}
